import 'dart:convert';
import 'package:flutter/material.dart';
import 'package:http/http.dart' as http;
import 'CryptoDetailPage.dart'; 

class CryptoList extends StatefulWidget {
  @override
  _CryptoListState createState() => _CryptoListState();
}

class _CryptoListState extends State<CryptoList> {
  List<dynamic> _cryptoData = [];

  @override
  void initState() {
    super.initState();
    _fetchCryptoData();
  }

  Future<void> _fetchCryptoData() async {
    try {
      final response = await http.get(Uri.parse('https://api.coinlore.net/api/tickers/'));
      if (response.statusCode == 200) {
        setState(() {
          var jsonData = jsonDecode(response.body);
          if (jsonData['data'] != null) {
            _cryptoData = jsonData['data'].take(10).toList(); // Ambil hanya 10 data pertama
            _cryptoData.forEach((crypto) {
              switch (crypto['name']) {
                case 'Bitcoin':
                crypto['logo_url'] = 'https://cdn.pixabay.com/photo/2021/04/30/16/47/btc-logo-6219386_1280.png';
                break;
                case 'Ethereum':
                crypto['logo_url'] = 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTzORbJU4MJLKsX-uJpbv8uJh-e8DYts0AJT48Hc-gf0kA504KOirqEYEbxn32JDWHKYYI&usqp=CAU';
                break;
                case 'Tether':
                crypto['logo_url'] = 'data:image/png;base64,';
                break;
                case 'Binance Coin':
                crypto['logo_url'] = 'data:image/png;base64,';
                break;
                case 'Solana':
                crypto['logo_url'] = 'data:image/png;base64,';
                break;
                case 'Staked Ether':
                crypto['logo_url'] = 'data:image/png;base64,';
                break;
                case 'USD Coin':
                crypto['logo_url'] = 'data:image/png;base64,';
                break;
                case 'TON Coin':
                crypto['logo_url'] = 'https://res.coinpaper.com/coinpaper/toncoin_ton_logo_c5fb8b4302.png';
                break;
                case 'XRP':
                crypto['logo_url'] = 'data:image/jpeg;base64,';
                break;
                case 'Dogecoin':
                crypto['logo_url'] = 'data:image/jpeg;base64,';
                break;
                default:
                  // Use a default logo URL if not specified for other cryptos
                  crypto['logo_url'] = ''; // Replace with your default logo URL if needed
              }
            });
          } else {
            throw Exception('Data is null or not in expected format');
          }
        });
      } else {
        throw Exception('Failed to load crypto data');
      }
    } catch (e) {
      print('Error: $e');
    }
  }

  void _navigateToCryptoDetail(dynamic crypto) {
    Navigator.push(
      context,
      MaterialPageRoute(
        builder: (context) => CryptoDetailPage(crypto: crypto),
      ),
    );
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Harga Crypto'),
        actions: [
          Padding(
            padding: const EdgeInsets.all(8.0),
            child: Center(
              child: Text(
                'Ahmad Irfan Fauzi',
                style: TextStyle(fontSize: 16.0),
              ),
            ),
          ),
        ],
      ),
      body: _cryptoData.isEmpty
          ? Center(child: CircularProgressIndicator())
          : ListView.builder(
              itemCount: _cryptoData.length,
              itemBuilder: (context, index) {
                final crypto = _cryptoData[index];
                // Ensure crypto and its properties are not null before accessing
                String cryptoName = crypto['name'] ?? 'N/A';
                String cryptoPrice = crypto['price_usd'] ?? 'N/A';
                String logoUrl = crypto['logo_url'] ?? ''; // Get logo URL
                // Adding 1 to index to display 1-based numbering
                int itemNumber = index + 1;
                return Padding(
                  padding: const EdgeInsets.symmetric(vertical: 8.0, horizontal: 16.0),
                  child: Card(
                    elevation: 4.0,
                    shape: RoundedRectangleBorder(
                      borderRadius: BorderRadius.circular(10.0),
                    ),
                    child: ListTile(
                      leading: CircleAvatar(
                        backgroundImage: NetworkImage(logoUrl),
                        radius: 25.0,
                      ),
                      title: Text(
                        '$itemNumber. $cryptoName',
                        style: TextStyle(fontWeight: FontWeight.bold),
                      ),
                      subtitle: Text('\$$cryptoPrice'),
                      trailing: Icon(Icons.arrow_forward_ios),
                      onTap: () {
                        _navigateToCryptoDetail(crypto);
                      },
                    ),
                  ),
                );
              },
            ),
    );
  }
}
